import React from "react";
import { useFormik } from "formik";

const ContactUsMui = () => {
  const formik = useFormik({
    initialValues: {
      fullName: "",
      email: "",
      phoneNumber: "",
    },
    onSubmit: (values) => {
      console.log(values);
      alert(JSON.stringify(values, null, 2));
    },
  });

  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
      <div style={{ display: "flex", width: "80%" }}>
        {/* Image component or placeholder for full-length picture */}
        {/* Replace 'imgSrc' with your image URL */}
        <img
          src="imgSrc" // Replace with your image URL
          alt="Full-length picture"
          style={{ width: "50%", height: "100%", objectFit: "cover" }}
        />

        <div style={{ flex: 1, paddingLeft: "20px" }}>
          <div style={{ maxWidth: "400px", margin: "0 auto" }}>
            <h4 style={{ textAlign: "center", fontWeight: "bold" }}>Contact Us</h4>
            <form onSubmit={formik.handleSubmit}>
              <div style={{ display: "flex", flexDirection: "column", gap: "20px" }}>
                <input
                  type="text"
                  id="fullName"
                  name="fullName"
                  placeholder="Full Name"
                  onChange={formik.handleChange}
                  value={formik.values.fullName}
                />
                <input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="Email"
                  onChange={formik.handleChange}
                  value={formik.values.email}
                />
                <input
                  type="tel"
                  id="phoneNumber"
                  name="phoneNumber"
                  placeholder="Phone Number"
                  onChange={formik.handleChange}
                  value={formik.values.phoneNumber}
                />
                <button
                  type="submit"
                  style={{
                    background: "#ff0000",
                    color: "#fff",
                    padding: "10px",
                    border: "none",
                    borderRadius: "5px",
                    cursor: "pointer",
                  }}
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContactUsMui;
