import React from "react";
import { useFormik } from "formik";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";

const ContactUsMui = () => {
  const formik = useFormik({
    initialValues: {
      fullName: "",
      email: "",
      phoneNumber: "",
    },
    onSubmit: (values) => {
      console.log(values);
      alert(JSON.stringify(values, null, 2));
    },
  });

  return (
    <Box
      sx={{
        background: "linear-gradient(135deg, #667eea 30%, #764ba2 90%)", // Adjust gradient colors and angles as needed
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        padding: "40px",
      }}
    >
      <Grid container spacing={3} justifyContent="center" alignItems="center">
        <Grid item xs={12}>
          <Typography
            variant="h4"
            gutterBottom
            style={{ textAlign: "center", color: "#fff", fontWeight: "bold" }}
          >
            Contact Us
          </Typography>
        </Grid>
        <Grid item xs={12} sm={6}>
          <form onSubmit={formik.handleSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  id="fullName"
                  name="fullName"
                  type="text"
                  label="Full Name"
                  variant="filled" // Change to variant="filled" for filled background
                  onChange={formik.handleChange}
                  value={formik.values.fullName}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  id="email"
                  name="email"
                  type="email"
                  label="Email"
                  variant="filled" // Change to variant="filled" for filled background
                  onChange={formik.handleChange}
                  value={formik.values.email}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  id="phoneNumber"
                  name="phoneNumber"
                  type="tel"
                  label="Phone Number"
                  variant="filled" // Change to variant="filled" for filled background
                  onChange={formik.handleChange}
                  value={formik.values.phoneNumber}
                />
              </Grid>
              <Grid item xs={12}>
                <Button
                  variant="contained"
                  color="secondary" // Change the color to your preferred vibe (e.g., 'secondary', 'error', 'warning', 'success')
                  fullWidth
                  type="submit"
                >
                  Submit
                </Button>
              </Grid>
            </Grid>
          </form>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ContactUsMui;
